import React, {Component} from 'react';
import axios from 'axios';
import { Button, Container, CssBaseline, TextField, Grid } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import { DataGrid } from '@mui/x-data-grid';
import { CSVLink } from "react-csv";
import './index.css';
const useStyles = theme => ({
    paper: {
      marginTop: theme.spacing(12),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    form: {
      width: '100%', 
      marginTop: theme.spacing(4),
    },
    submit: {
      margin: theme.spacing(3, 0, 3),
    },
});


const columns = [
  { field: '_id', headerName: 'id', width: 200 },
  { field: 'product', headerName: 'Product', width: 150 },
  { field: 'amount', headerName: 'Amount', width: 150 },
  { field: 'vendor', headerName: 'vendor', width: 150 },
];

class Inventory extends Component {
    csvLink = React.createRef()

    constructor(props) {
        super(props);

        this.state = {
            id: '',
            idDelete: '',
            product: '',
            productUpdate: '',
            amount: '',
            amountUpdate: '',
            vendor: '',
            vendorUpdate: '',
            inventory: [],
            data: []
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
        this.getTransactionData = this.getTransactionData.bind(this)
    }

    componentDidMount() {
      axios.get("http://localhost:5000")
        .then(res => this.setState({inventory: res.data}))
    }

    getTransactionData = async () => {
      await axios.get("http://localhost:5000/download")
        .then(res => this.setState({data: res.data}))
        .catch((e) => console.log(e))
      this.csvLink.current.link.click()
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
        console.log(e.target.value);
    }
    onChange2 = (e) => {
      this.setState({ [e.target.name]: e.target.value });
      console.log(e.target.value);
  }
    onDropdownSelected = (e) => {
      console.log("THE VAL", e.target.value);
      this.setState({ [e.target.name]: e.target.value });
      //here you will see the current selected value of the select input
  }

    onSubmit(e) {

        const inventory = {
            product: this.state.product,
            amount: this.state.amount,
            color: this.state.color,
            vendor: this.state.vendor
        }

        console.log(inventory)

        if (e.nativeEvent.submitter.name === "add") {
          axios.post("http://localhost:5000/add", inventory)
            .then(res => console.log(res.data));
        }

        if (e.nativeEvent.submitter.name === "update") {
          console.log('from frontend' + this.state.id)
          axios.patch("http://localhost:5000/"+this.state.id, inventory)
            .then(res => console.log(res.data));
        }

        if (e.nativeEvent.submitter.name === "delete"){
          axios.delete("http://localhost:5000/"+this.state.idDelete, inventory)
            .then(res => console.log(res.data));
        }

        axios.get("http://localhost:5000")
        .then(res => this.setState({inventory: res.data}))
    }


    render() {
        const { classes } = this.props
        const dropdown = this.state.inventory.map((item, i) => {
          return (
            <option key={i} value={this.state.id} onChange={this.onChange}>{item._id}</option>
          )
        }, this);
        return(
          <div>
          <h1>Shopify Inventory Tracker Challenge</h1>

          <Container component="main" maxWidth="md">
            {/* <CssBaseline /> */}
            <div style={{ height: 800, width: '75%' }}>
                <DataGrid rows={this.state.inventory} columns={columns} />
              </div>
              <br></br>
              <h2> Add Items</h2>
              <Grid item xs={8}>
                <form className={classes.form} noValidate onSubmit={this.onSubmit}>
                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        id="product"
                        label="Product"
                        name="product"
                        autoFocus
                        value={this.state.product}
                        onChange={this.onChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        id="amount"
                        label="Amount"
                        name="amount"
                        value={this.state.amount}
                        onChange={this.onChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        name="color"
                        label="Color"
                        id="color"
                        value={this.state.color}
                        onChange={this.onChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        name="vendor"
                        label="Vendor"
                        type="vendor"
                        id="vendor"
                        value={this.state.vendor}
                        onChange={this.onChange}
                      />
                    </Grid>
                  </Grid>
                  <Grid container>
                    <Grid item xs={3}>
                      <Button
                        type="submit"
                        name="add"
                        variant="contained"
                        className={classes.submit}
                      >
                        Add Item
                      </Button>
                    </Grid>
                  </Grid>
                </form>
              </Grid>

              <div>
                <h2> Update Item</h2>
                <h4> Select the item that needs to be updated below</h4>
                <select name = "id" onChange={this.onDropdownSelected}>
                <option> - </option>
                {this.state.inventory.map((item, i) => {
          return (
            <option key={i}  value={item._id}>{item._id}</option>
          )
        })}

                </select>
                <Grid item xs={8}>
                <form className={classes.form} noValidate onSubmit={this.onSubmit}>
                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        id="product_change"
                        label="Product"
                        name="productUpdate"
                        value={this.state.productUpdate}
                        onChange={this.onChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        id="amount"
                        label="Amount"
                        name="amount"
                        value={this.state.amount}
                        onChange={this.onChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        name="color"
                        label="Color"
                        id="color"
                        value={this.state.color}
                        onChange={this.onChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        name="vendor"
                        label="Vendor"
                        type="vendor"
                        id="vendor"
                        value={this.state.vendor}
                        onChange={this.onChange}
                      />
                    </Grid>
                  </Grid>
